"""
Intent classifier with semantic NLP capabilities.

MIGRATED: Now uses semantic core instead of hardcoded patterns.
Maintains backward compatibility with existing interface.
"""
import re
import unicodedata
from typing import Dict, Optional, Tuple
from dataclasses import dataclass

from ...domain.agents.agent_protocol import Intent
from ...core.semantics import SemanticIntentService, IntentResult, get_global_context_manager
from ...core.logging import get_logger

logger = get_logger(__name__)


@dataclass
class IntentClassificationResult:
    """Result of intent classification."""
    intent: Intent
    confidence: float
    matched_patterns: list[str]
    reasoning: str


class IntentClassifier:
    """
    Semantic intent classifier.

    MIGRATED: Now uses SemanticIntentService for intelligent classification.
    Maintains backward compatibility with legacy interface.
    """

    _INTENT_COMPAT_ALIASES = {
        Intent.SUMMARY: Intent.SUMMARY_REQUEST,
        Intent.BRANCH: Intent.BRANCH_QUERY,
        Intent.ANOMALY: Intent.ANOMALY_QUERY,
    }

    def __init__(self, session_id: str = "global"):
        """
        Initialize the classifier with semantic NLP.

        Args:
            session_id: Session ID for context management
        """
        self.session_id = session_id
        self.semantic_service = SemanticIntentService()
        self.context_manager = get_global_context_manager()

        # Legacy patterns kept for backward compatibility and testing
        self._legacy_patterns = self._initialize_legacy_patterns()

        logger.info({"event": "intent_classifier_initialized",
                    "session_id": session_id,
                    "mode": "semantic_nlp"})
    
    def classify(self, query: str, use_semantic: bool = True) -> IntentClassificationResult:
        """
        Classify user intent using semantic NLP.

        Args:
            query: User query string
            use_semantic: Whether to use semantic classification (default: True)

        Returns:
            IntentClassificationResult with detected intent
        """
        if not query or not query.strip():
            return IntentClassificationResult(
                intent=Intent.UNKNOWN,
                confidence=1.0,
                matched_patterns=[],
                reasoning="Empty or whitespace-only query"
            )

        if use_semantic:
            return self._semantic_classify(query)
        else:
            # Fallback to legacy patterns for testing/comparison
            return self._legacy_classify(query)

    def _normalize_intent(self, intent: Intent | None) -> Intent:
        """Map semantic intents to legacy-compatible aliases."""
        if intent is None:
            return Intent.UNKNOWN
        return self._INTENT_COMPAT_ALIASES.get(intent, intent)

    def _semantic_classify(self, query: str) -> IntentClassificationResult:
        """Classify using semantic NLP system"""
        try:
            context = self.context_manager.get_context_summary(self.session_id)
            semantic_result = self.semantic_service.classify_intent(query, context)
            normalized_intent = self._normalize_intent(semantic_result.intent)

            semantic_patterns = [f"semantic_pattern:{normalized_intent.value if normalized_intent else 'unknown'}"]
            semantic_reasoning = (
                f"Semantic pattern match: {semantic_result.reasoning} "
                f"(confidence={semantic_result.confidence:.3f}, entities={len(semantic_result.entities)})"
            )
            semantic_response = IntentClassificationResult(
                intent=normalized_intent,
                confidence=semantic_result.confidence,
                matched_patterns=semantic_patterns,
                reasoning=semantic_reasoning
            )

            legacy_candidate = self._legacy_classify(query)
            if legacy_candidate.intent != Intent.UNKNOWN and legacy_candidate.matched_patterns:
                legacy_better = legacy_candidate.confidence >= max(semantic_result.confidence, 0.35)
                semantic_uncertain = normalized_intent in {Intent.UNKNOWN, Intent.FILE_OPERATION} and legacy_candidate.intent != Intent.UNKNOWN
                if legacy_better or (semantic_uncertain and legacy_candidate.confidence >= 0.05):
                    combined_reasoning = (
                        f"Legacy pattern match preferred: {legacy_candidate.reasoning} | "
                        f"semantic suggested {normalized_intent.value if normalized_intent else 'unknown'} "
                        f"({semantic_result.confidence:.3f})"
                    )
                    return IntentClassificationResult(
                        intent=legacy_candidate.intent,
                        confidence=max(legacy_candidate.confidence, semantic_result.confidence),
                        matched_patterns=legacy_candidate.matched_patterns,
                        reasoning=combined_reasoning
                    )

                if legacy_candidate.intent == normalized_intent:
                    combined_patterns = list(dict.fromkeys(semantic_patterns + legacy_candidate.matched_patterns))
                    combined_reasoning = (
                        semantic_reasoning
                        + f" | legacy patterns matched: {', '.join(legacy_candidate.matched_patterns)}"
                    )
                    return IntentClassificationResult(
                        intent=normalized_intent,
                        confidence=max(semantic_result.confidence, legacy_candidate.confidence),
                        matched_patterns=combined_patterns,
                        reasoning=combined_reasoning
                    )

            if normalized_intent is None or normalized_intent == Intent.UNKNOWN:
                return self._legacy_classify(query)

            return semantic_response

        except Exception as e:
            logger.error({"event": "semantic_classification_error", "query": query, "error": str(e)})
            logger.warning("Falling back to legacy classification due to semantic error")
            return self._legacy_classify(query)

    def _legacy_classify(self, query: str) -> IntentClassificationResult:
        """Legacy pattern-based classification for fallback"""
        query_lower = (query or "").lower().strip()
        variants = []

        if query_lower:

            variants.append(query_lower)

            for source, target in (("latin-1", "utf-8"), ("utf-8", "latin-1")):

                try:

                    repaired = query_lower.encode(source, errors="ignore").decode(target, errors="ignore")

                    if repaired and repaired not in variants:

                        variants.append(repaired)

                except UnicodeError:

                    continue

        for candidate in list(variants):
            normalized = "".join(
                ch for ch in unicodedata.normalize("NFKD", candidate) if not unicodedata.combining(ch)
            )
            if normalized and normalized not in variants:
                variants.append(normalized)
            ascii_candidate = " ".join(
                "".join(
                    ch if ("a" <= ch <= "z") or ch.isspace() else " " for ch in normalized.lower()
                ).split()
            )
            if ascii_candidate and ascii_candidate not in variants:
                variants.append(ascii_candidate)



        if not variants:
            variants.append(query_lower)

        # Score each intent based on pattern matches
        intent_scores: Dict[Intent, Tuple[float, list[str]]] = {}

        # Priority weights for different intents
        intent_priority = {
            Intent.FILE_OPERATION: 2.0,
            Intent.SUMMARY_REQUEST: 1.5,
            Intent.BRANCH_QUERY: 1.5,
            Intent.ANOMALY_QUERY: 1.5,
            Intent.GREETING: 0.5,
            Intent.SMALL_TALK: 0.3,
        }

        for intent, patterns in self._legacy_patterns.items():
            matched_patterns = []
            total_matches = 0

            for pattern in patterns:
                matches = []
                for candidate in variants:
                    matches = re.findall(pattern, candidate, re.IGNORECASE)
                    if matches:
                        break
                if matches:
                    matched_patterns.append(pattern)
                    total_matches += len(matches)

            if total_matches > 0:
                base_score = min(total_matches / max(len(query_lower.split()), 1), 1.0)
                priority_weight = intent_priority.get(intent, 1.0)
                final_score = min(base_score * priority_weight, 1.0)
                intent_scores[intent] = (final_score, matched_patterns)

        if not intent_scores:
            return IntentClassificationResult(
                intent=Intent.UNKNOWN,
                confidence=1.0,
                matched_patterns=[],
                reasoning="No legacy patterns matched"
            )

        # Find the highest scoring intent
        best_intent, (best_score, best_patterns) = max(
            intent_scores.items(),
            key=lambda x: x[1][0]
        )

        return IntentClassificationResult(
            intent=best_intent,
            confidence=best_score,
            matched_patterns=best_patterns,
            reasoning=f"Legacy: Matched {len(best_patterns)} pattern(s) with score {best_score:.2f}"
        )
    
