    # Backward compatibility methods
    def add_pattern(self, intent: Intent, pattern: str) -> None:
        """Add pattern to legacy patterns (backward compatibility)"""
        if intent not in self._legacy_patterns:
            self._legacy_patterns[intent] = []

        if pattern not in self._legacy_patterns[intent]:
            self._legacy_patterns[intent].append(pattern)

        logger.info({"event": "legacy_pattern_added",
                    "intent": intent.value,
                    "pattern": pattern})

    def get_patterns(self, intent: Intent) -> list[str]:
        """Get legacy patterns for intent (backward compatibility)"""
        return self._legacy_patterns.get(intent, []).copy()

    def get_supported_intents(self) -> list[Intent]:
        """Get supported intents (backward compatibility)"""
        return list(self._legacy_patterns.keys())

    # New semantic methods
    def get_semantic_result(self, query: str) -> IntentResult:
        """
        Get full semantic classification result

        Returns:
            IntentResult with complete semantic analysis
        """
        context = self.context_manager.get_context_summary(self.session_id)
        return self.semantic_service.classify_intent(query, context)

    def track_file_access(self, filename: str, operation: str) -> None:
        """Track file access for context management"""
        self.context_manager.track_file_access(
            self.session_id,
            filename,
            operation
        )

