    def test_anomaly_query_intent(self, classifier):
        """Test anomaly query intent classification."""
        # Test cases that should definitively work
        definitive_cases = [
            "Detectar anomalias",
            "Find anomalies", 
            "Outliers en los datos",
            "Transacciones sospechosas"
        ]
        
        for query in definitive_cases:
            result = classifier.classify(query, use_semantic=False)
            assert result.intent == Intent.ANOMALY_QUERY
            assert result.confidence > 0
            assert len(result.matched_patterns) > 0
        
        # Test an edge case that may or may not work (acceptable either way)
        edge_result = classifier.classify("Datos irregulares", use_semantic=False)
        assert edge_result.intent in [Intent.ANOMALY_QUERY, Intent.UNKNOWN]
    
    def test_small_talk_intent(self, classifier):
        """Test small talk intent classification."""
        test_cases = [
            "Gracias",
            "Thank you very much",
            "Perfecto",
            "Excelente trabajo",
            "Adios"
        ]

        for query in test_cases:
            result = classifier.classify(query, use_semantic=False)
            assert result.intent == Intent.SMALL_TALK
            assert result.confidence > 0
            assert len(result.matched_patterns) > 0

