    def test_empty_query(self, classifier):
        """Test classification of empty queries."""
        test_cases = ["", "   ", "\t\n", None]
        
        for query in test_cases:
            if query is None:
                continue  # Skip None for now
            result = classifier.classify(query, use_semantic=False)
            assert result.intent == Intent.UNKNOWN
            assert result.confidence == 1.0
            assert len(result.matched_patterns) == 0
            assert "empty" in result.reasoning.lower()
    
    def test_mixed_intent_highest_score_wins(self, classifier):
        """Test that highest scoring intent wins for mixed queries."""
        # Query that could match multiple intents but summary should win
        query = "Mostrar resumen de sucursales"
        result = classifier.classify(query, use_semantic=False)
        
        # Should classify as summary since it has more specific match
        assert result.intent in [Intent.SUMMARY_REQUEST, Intent.BRANCH_QUERY]
        assert result.confidence > 0
        assert len(result.matched_patterns) > 0
    
    def test_add_custom_pattern(self, classifier):
        """Test adding custom patterns."""
        # Add a custom pattern for greetings
        custom_pattern = r'\b(howdy|hey there)\b'
        classifier.add_pattern(Intent.GREETING, custom_pattern)
        
        # Test that the new pattern works
