    def test_case_insensitive_matching(self, classifier):
        """Test that pattern matching is case insensitive."""
        test_cases = [
            ("HOLA", Intent.GREETING),
            ("resumen", Intent.SUMMARY_REQUEST),
            ("ANOMALIAS", Intent.ANOMALY_QUERY),
            ("Sucursal", Intent.BRANCH_QUERY)
        ]
        
        for query, expected_intent in test_cases:
            result = classifier.classify(query, use_semantic=False)
            assert result.intent == expected_intent